// App.js

import React, { useState } from 'react';
import './App.css'; // Adjust the path to match your actual CSS file
import Forms from './Forms'; // Import the Forms component

const App = () => {
    const [showLoginForm, setShowLoginForm] = useState(false);
    const [showSignUpForm, setShowSignUpForm] = useState(false);
    const [showRoleForm, setShowRoleForm] = useState(false);
    const [selectedRole, setSelectedRole] = useState('');
    const [showInviteCodeForm, setShowInviteCodeForm] = useState(false);

    const handleLoginClick = () => {
        setShowLoginForm(true);
        setShowSignUpForm(false);
        setShowRoleForm(false);
        setShowInviteCodeForm(false);
    };

    const handleSignUpClick = () => {
        setShowLoginForm(false);
        setShowSignUpForm(true);
        setShowRoleForm(false);
        setShowInviteCodeForm(false);
    };

    const handleToggleForm = () => {
        setShowLoginForm(!showLoginForm);
        setShowSignUpForm(!showSignUpForm);
        setShowRoleForm(false);
        setShowInviteCodeForm(false);
    };

    const handleRoleChange = (e) => {
        setSelectedRole(e.target.value);
    };

    const handleRoleFormSubmit = (e) => {
        e.preventDefault();
        setShowRoleForm(false);
        setShowInviteCodeForm(true); // Show the invite code form after role selection
    };

    const handleInviteCodeSubmit = (e) => {
        e.preventDefault();
        // Handle invite code submission logic here
        console.log('Invite code submitted');
        // Optionally, navigate to the dashboard or next step
    };

    return (
        <div>
            <section id="section1">
                <header>Welcome</header>
                <p>Hello world for now!</p>
                <button onClick={handleLoginClick}>Login</button>
                <button onClick={handleSignUpClick}>Sign Up</button>
            </section>

            {/* Forms component */}
            <Forms
                showLoginForm={showLoginForm}
                showSignUpForm={showSignUpForm}
                showRoleForm={showRoleForm}
                selectedRole={selectedRole}
                handleToggleForm={handleToggleForm}
                handleRoleChange={handleRoleChange}
                handleRoleFormSubmit={handleRoleFormSubmit}
                handleSignupFormSubmit={handleSignupFormSubmit}
            />

            {/* Invite Code Form */}
            {showInviteCodeForm && (
                <div className="form-container">
                    <h2>Invite Code</h2>
                    <form onSubmit={handleInviteCodeSubmit}>
                        <label htmlFor="invite-code">Enter Invite Code:</label>
                        <input type="text" id="invite-code" name="invite-code" required />
                        <button type="submit">Submit</button>
                    </form>
                </div>
            )}
        </div>
    );
};

export default App;
