import React, { useState } from 'react';
import './Dashboard.css';  // Import CSS for styling

// Generate a unique invite code
const generateInviteCode = () => {
    return Math.random().toString(36).substr(2, 9);
};

// // Sample user data
// const users = [
//     {
//         id: 1,
//         firstname: 'John',
//         lastname: 'Doe',
//         dob: '1990-01-01',
//         gender: 'Male',
//         role: 'Provider',
//         licenseNumber: '12345',
//         isLoggedIn: true,
//         dashboard: 'ProviderDashboard',
//         inviteCode: generateInviteCode(),
//         connections: [],
//         circles: []
//     },
//     {
//         id: 2,
//         firstname: 'Jane',
//         lastname: 'Doe',
//         dob: '1992-02-02',
//         gender: 'Female',
//         role: 'Pregnant',
//         weeksPregnant: 24,
//         isLoggedIn: false,
//         dashboard: 'PregnantDashboard',
//         inviteCode: generateInviteCode(),
//         connections: [],
//         circles: []
//     }
//     // Add more users as needed
// ];

// Menu component for displaying navigation buttons
const Menu = ({ onSelect }) => {
    return (
        <div className="menu">
            <button onClick={() => onSelect('profile')}>Profile</button>
            <button onClick={() => onSelect('communityFeed')}>Community Feed</button>
            <button onClick={() => onSelect('mapCalendar')}>Map/Calendar</button>
            <button onClick={() => onSelect('safety')}>Safety</button>
            <button onClick={() => onSelect('notebook')}>Notebook</button>
        </div>
    );
};

// Form component for submitting invite codes
const InviteCodeForm = ({ onInviteCodeSubmit }) => {
    const [inviteCode, setInviteCode] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        onInviteCodeSubmit(inviteCode);
        setInviteCode('');
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                placeholder="Enter invite code"
                value={inviteCode}
                onChange={(e) => setInviteCode(e.target.value)}
            />
            <button type="submit">Submit</button>
        </form>
    );
};

const NewsFeed = () => {
    return <div>News Feed Content</div>;
};

// Placeholder component for Community Feed content
const CommunityFeed = ({ onInviteCodeSubmit }) => {
    return (
        <div>
            <div>Community Feed Content</div>
            <NewsFeed />
            <InviteCodeForm onInviteCodeSubmit={onInviteCodeSubmit} />
            {/* Add other community feed features here */}
        </div>
    );
};

// Placeholder component for Profile content
const Profile = ({ user }) => {
    return (
        <div className="profile">
            <h2>Profile</h2>
            <p>First Name: {user.firstname}</p>
            <p>Last Name: {user.lastname}</p>
            <p>Date of Birth: {user.dob}</p>
            <p>Gender: {user.gender}</p>
            <p>Role: {user.role}</p>
            {user.role === 'Provider' && <p>License Number: {user.licenseNumber}</p>}
            {user.role === 'Pregnant' && <p>Weeks Pregnant: {user.weeksPregnant}</p>}
            <p>Invite Code: {user.inviteCode}</p>
        </div>
    );
};

const MapCalendar = () => {
    return <div>Map/Calendar Content</div>;
};

const Safety = () => {
    return <div>Safety Content</div>;
};

const Notebook = () => {
    return <div>Notebook Content</div>;
};

// Main layout component for displaying selected content area
const DashboardLayout = ({ user }) => {
    const [selectedSection, setSelectedSection] = useState('communityFeed');

    const onInviteCodeSubmit = (inviteCode) => {
        const invitedUser = users.find(user => user.inviteCode === inviteCode);
        if (invitedUser) {
            // Notify the invited user
            alert(`${invitedUser.firstname} has been notified of your connection request.`);
            // Logic to notify the user and handle acceptance
        } else {
            alert('Invalid invite code.');
        }
    };

    // Function to render content based on selected section
    const renderContent = () => {
        switch (selectedSection) {
            case 'profile':
                return <Profile user={user} />;
            case 'communityFeed':
                return <CommunityFeed onInviteCodeSubmit={onInviteCodeSubmit} />;
            case 'mapCalendar':
                return <MapCalendar />;
            case 'safety':
                return <Safety />;
            case 'notebook':
                return <Notebook />;
            default:
                return <CommunityFeed onInviteCodeSubmit={onInviteCodeSubmit} />;
        }
    };

    return (
        <div className="dashboard-layout">
            <Menu onSelect={setSelectedSection} />
            <div className="content-area">
                {renderContent()}
            </div>
        </div>
    );
};

const ProviderDashboard = ({ user, onInviteCodeSubmit }) => {
    return <DashboardLayout user={user} onInviteCodeSubmit={onInviteCodeSubmit} />;
};

const PregnantDashboard = ({ user, onInviteCodeSubmit }) => {
    return <DashboardLayout user={user} onInviteCodeSubmit={onInviteCodeSubmit} />;
};

// Dashboards component to switch between different dashboards
const Dashboards = ({ user }) => {
    const handleInviteCodeSubmit = (inviteCode) => {
        const invitedUser = users.find(u => u.inviteCode === inviteCode);
        if (invitedUser) {
            alert(`${invitedUser.firstname} has been notified of your connection request.`);
            // Add logic to notify the invited user and handle acceptance
        } else {
            alert('Invalid invite code.');
        }
    };

    switch (user.dashboard) {
        case 'ProviderDashboard':
            return <ProviderDashboard user={user} onInviteCodeSubmit={handleInviteCodeSubmit} />;
        case 'PregnantDashboard':
            return <PregnantDashboard user={user} onInviteCodeSubmit={handleInviteCodeSubmit} />;
        default:
            return <div>No Dashboard Found</div>;
    }
};

export { ProviderDashboard, PregnantDashboard, Dashboards };
