import React, { useState } from 'react';
import { Calendar, dateFnsLocalizer } from 'react-big-calendar';
import { format, parse, startOfWeek, getDay } from 'date-fns';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import enUS from 'date-fns/locale/en-US';
import './MapCalendar.css';

const locales = { 'en-US': enUS };

const localizer = dateFnsLocalizer({
    format,
    parse,
    startOfWeek,
    getDay,
    locales,
});

const MapCalendar = ({ user, users, setUsers }) => {
    const [events, setEvents] = useState([]);
    const [newEvent, setNewEvent] = useState({ title: '', start: '', end: '' });

    const handleAddEvent = () => {
        setEvents([...events, newEvent]);
        setNewEvent({ title: '', start: '', end: '' });
    };

    const handleDeleteEvent = (eventToDelete) => {
        setEvents(events.filter(event => event !== eventToDelete));
    };

    const handleRequestAppointment = (inviteCode) => {
        const invitedUser = users.find(user => user.inviteCode === inviteCode.trim());
        if (invitedUser) {
            alert(`Request sent to ${invitedUser.firstname} ${invitedUser.lastname}`);
        } else {
            alert('Invalid invite code.');
        }
    };

    return (
        <div className="map-calendar">
            <div className="calendar-section">
                <h2>Calendar</h2>
                <div className="date-picker-container">
                    <label>Start Date and Time:</label>
                    <input
                        type="text"
                        value={newEvent.start}
                        onChange={(e) => setNewEvent({ ...newEvent, start: e.target.value })}
                        placeholder="dd/mm/yyyy hh/mm"
                        className="datetime-input"
                    />
                    <label>End Date and Time:</label>
                    <input
                        type="text"
                        value={newEvent.end}
                        onChange={(e) => setNewEvent({ ...newEvent, end: e.target.value })}
                        placeholder="dd/mm/yyyy hh/mm"
                        className="datetime-input"
                    />
                </div>
                <input
                    type="text"
                    placeholder="Title"
                    value={newEvent.title}
                    onChange={(e) => setNewEvent({ ...newEvent, title: e.target.value })}
                />
                <button onClick={handleAddEvent}>Add Event</button>
                <Calendar
                    localizer={localizer}
                    events={events}
                    startAccessor="start"
                    endAccessor="end"
                    views={['month', 'agenda']}
                    style={{ height: 500, margin: '20px 0' }}
                    selectable
                    onSelectSlot={(slotInfo) => {
                        setNewEvent({
                            ...newEvent,
                            start: slotInfo.start,
                            end: slotInfo.end,
                        });
                    }}
                    onSelectEvent={(event) => {
                        alert(`Selected event: ${event.title}`);
                    }}
                    formats={{
                        timeGutterFormat: 'HH:mm',
                        eventTimeRangeFormat: ({ start, end }, culture, localizer) =>
                            `${localizer.format(start, 'HH:mm', culture)} - ${localizer.format(end, 'HH:mm', culture)}`,
                    }}
                />
                <div className="event-list">
                    {events.map((event, index) => (
                        <div key={index} className="event-item">
                            <span>{event.title}</span>
                            <button onClick={() => handleDeleteEvent(event)}>Delete</button>
                        </div>
                    ))}
                </div>
            </div>
            <div className="map-section">
                <h2>Map</h2>
                {/* Add your map component or integration here */}
            </div>
        </div>
    );
};

export default MapCalendar;
