import React, { useState } from 'react';
import { Calendar, dateFnsLocalizer } from 'react-big-calendar';
import { format, parse, startOfWeek, getDay } from 'date-fns';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import './CalendarComponent.css'; // Make sure to define your styles here

const locales = {
    'en-US': require('date-fns/locale/en-US'),
};

const localizer = dateFnsLocalizer({
    format,
    parse,
    startOfWeek,
    getDay,
    locales,
});

const CalendarComponent = () => {
    const [events, setEvents] = useState([]);
    const [newEvent, setNewEvent] = useState({ title: '', start: '', end: '' });

    const handleAddEvent = () => {
        setEvents([...events, newEvent]);
        setNewEvent({ title: '', start: '', end: '' });
    };

    const handleDeleteEvent = (eventToDelete) => {
        setEvents(events.filter(event => event !== eventToDelete));
    };

    return (
        <div className="calendar-container">
            <div className="calendar-box">
                <h2>Calendar</h2>
                <DatePicker
                    selected={newEvent.start}
                    onChange={(start) => setNewEvent({ ...newEvent, start })}
                    showTimeSelect
                    timeFormat="HH:mm"
                    timeIntervals={15}
                    dateFormat="MMMM d, yyyy h:mm aa"
                    placeholderText="Select Start Date and Time"
                />
                <input
                    type="text"
                    placeholder="Title"
                    value={newEvent.title}
                    onChange={(e) => setNewEvent({ ...newEvent, title: e.target.value })}
                />
                <button onClick={handleAddEvent}>Add Event</button>
                <div className="appointment-list">
                    <h3>Appointments</h3>
                    <ul>
                        {events.map((event, index) => (
                            <li key={index}>
                                {event.title} - {new Date(event.start).toLocaleString()}
                                <button onClick={() => handleDeleteEvent(event)}>Delete</button>
                            </li>
                        ))}
                    </ul>
                </div>
            </div>
            <div className="map-box">
                <h2>Map</h2>
                {/* Add your map component or integration here */}
            </div>
        </div>
    );
};

export default CalendarComponent;
