import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet'; // Import Leaflet library (needed for creating custom icons)
import 'leaflet/dist/leaflet.css';

// Import your custom icon images
// import hospitalIcon from './icons/hospital-icon.png';
// import gymIcon from './icons/gym-icon.png';
// import socialServicesIcon from './icons/social-services-icon.png';
import icon1 from './pin.png'

const Map = () => {
    // Coordinates for 10027 (New York)
    const center = [40.811550, -73.953300];

    // Sample locations for hospitals, gyms, and social services
    const locations = [
        { name: 'Hospital 1', position: [40.810, -73.950], icon: hospitalIcon },
        { name: 'Hospital 2', position: [40.805, -73.955], icon: hospitalIcon },
        { name: 'Gym 1', position: [40.815, -73.945], icon: gymIcon },
        { name: 'Department of Social Services', position: [40.812, -73.960], icon: socialServicesIcon }
    ];

    // Custom icons configuration
    const hospitalMarker = new L.Icon({
        iconUrl: icon1,
        iconSize: [32, 32], // size of the icon
        iconAnchor: [16, 32], // point of the icon which will correspond to marker's location
        popupAnchor: [0, -32] // point from which the popup should open relative to the iconAnchor
    });

    // const gymMarker = new L.Icon({
    //     iconUrl: gymIcon,
    //     iconSize: [32, 32],
    //     iconAnchor: [16, 32],
    //     popupAnchor: [0, -32]
    // });

    // const socialServicesMarker = new L.Icon({
    //     iconUrl: socialServicesIcon,
    //     iconSize: [32, 32],
    //     iconAnchor: [16, 32],
    //     popupAnchor: [0, -32]
    // });

    return (
        <div className="dashboard-section" style={{ float: 'right', width: '50%', height: '500px' }}>
            <h2>Map</h2>
            <MapContainer center={center} zoom={14} style={{ width: '100%', height: '100%' }}>
                <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                    maxZoom={18}
                />
                {/* Render markers for each location */}
                {locations.map((location, index) => (
                    <Marker key={index} position={location.position} icon={getMarkerIcon(location.icon)}>
                        <Popup>{location.name}</Popup>
                    </Marker>
                ))}
            </MapContainer>
        </div>
    );
};

// Helper function to return appropriate marker icon
const getMarkerIcon = (iconUrl) => {
    return new L.Icon({
        iconUrl: iconUrl,
        iconSize: [32, 32],
        iconAnchor: [16, 32],
        popupAnchor: [0, -32]
    });
};

export default Map;
