import React, { useState } from 'react';
import { SignUpForm, LoginForm } from './Forms';

const App = () => {
  const [currentPage, setCurrentPage] = useState('home'); // 'home', 'login', 'signup', 'dashboard'

  const handleSignupFormSubmit = (e) => {
    e.preventDefault();
    // Handle sign up form submission logic
    console.log('Sign Up form submitted');
    setCurrentPage('dashboard'); // Example: Change to dashboard page after signup
  };

  const handleLoginFormSubmit = (e) => {
    e.preventDefault();
    // Handle login form submission logic
    console.log('Login form submitted');
    setCurrentPage('dashboard'); // Example: Change to dashboard page after login
  };

  const handleLoginClick = () => {
    // Handle login button click
    setCurrentPage('login');
  };

  const handleSignupClick = () => {
    // Handle sign up button click
    setCurrentPage('signup');
  };

  return (
    <div>
      {/* Home/Landing Page */}
      {currentPage === 'home' && (
        <section id="section1">
          <header>Welcome</header>
          <p>Hello world for now!</p>
          <button onClick={() => setCurrentPage('login')}>Login</button>
          <button onClick={() => setCurrentPage('signup')}>Sign Up</button>
        </section>
      )}

      {/* Login Form */}
      {currentPage === 'login' && (
        <LoginForm handleLoginFormSubmit={handleLoginFormSubmit} handleSignupClick={handleSignupClick} />
      )}

      {/* SignUp Form */}
      {currentPage === 'signup' && (
        <SignUpForm handleSignupFormSubmit={handleSignupFormSubmit} handleLoginClick={handleLoginClick} />
      )}

      {/* Dashboard */}
      {currentPage === 'dashboard' && (
        <section id="sectiondashboard">
          <div id="dashboard">
            <header>Dashboard</header>
            <p>Welcome to your dashboard!</p>
          </div>
        </section>
      )}
    </div>
  );
};

export default App;
