import React, { useState } from 'react';
import './MapCalendar.css';
import { Calendar, dateFnsLocalizer } from 'react-big-calendar';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import { format } from 'date-fns';
import enUS from 'date-fns/locale/en-US';

const locales = { 'en-US': enUS };

const localizer = dateFnsLocalizer({
    format,
    locales,
});

const MapCalendar = ({ user, users, setUsers }) => {
    const [events, setEvents] = useState([]);
    const [selectedDate, setSelectedDate] = useState(new Date());
    const [eventDate, setEventDate] = useState('');
    const [eventTime, setEventTime] = useState('');
    const [eventDescription, setEventDescription] = useState('');

    const handleMonthChange = (date) => {
        setSelectedDate(date);
    };

    const handleAddEvent = () => {
        if (eventDate && eventTime && eventDescription) {
            const newEvent = {
                title: eventDescription,
                start: new Date(eventDate.setHours(eventTime.getHours(), eventTime.getMinutes())),
                end: new Date(eventDate.setHours(eventTime.getHours() + 1, eventTime.getMinutes())),
            };
            setEvents([...events, newEvent]);
            setEventDate('');
            setEventTime('');
            setEventDescription('');
        } else {
            alert('Please fill out all event details.');
        }
    };

    const handleDeleteEvent = (eventToDelete) => {
        setEvents(events.filter(event => event !== eventToDelete));
    };

    const handleDateChange = (date) => {
        setEventDate(date);
    };

    const handleTimeChange = (time) => {
        setEventTime(time);
    };

    return (
        <div className="map-calendar">
            <div className="calendar-section">
                <h2>Calendar</h2>
                <div className="event-form">
                    <div className="event-input">
                        <label>Date:</label>
                        <DatePicker
                            selected={eventDate}
                            onChange={handleDateChange}
                            dateFormat="dd/MM/yyyy"
                            placeholderText="Select date"
                        />
                    </div>
                    <div className="event-input">
                        <label>Time:</label>
                        <DatePicker
                            selected={eventTime}
                            onChange={handleTimeChange}
                            showTimeSelect
                            showTimeSelectOnly
                            timeIntervals={15}
                            dateFormat="HH:mm"
                            placeholderText="Select time"
                        />
                    </div>
                    <div className="event-input">
                        <label>Description:</label>
                        <input
                            type="text"
                            value={eventDescription}
                            onChange={(e) => setEventDescription(e.target.value)}
                            placeholder="Enter description"
                        />
                    </div>
                    <button onClick={handleAddEvent}>Add Event</button>
                </div>
                <Calendar
                    localizer={localizer}
                    events={events}
                    startAccessor="start"
                    endAccessor="end"
                    style={{ height: 500, marginTop: 20 }}
                />
                <div className="event-list">
                    <h3>Events:</h3>
                    {events.map((event, index) => (
                        <div className="event-item" key={index}>
                            <span>{event.title}</span>
                            <button onClick={() => handleDeleteEvent(event)}>Delete</button>
                        </div>
                    ))}
                </div>
            </div>
            <div className="map-section">
                <h2>Map</h2>
                {/* Placeholder for map integration */}
            </div>
        </div>
    );
};

export default MapCalendar;
