import './App.css';
import { useState } from 'react';

//component is a piece of code that returns or renders JSX
const Person = (props) => {
  return (
    <>
    <h1> Name: {props.name}</h1>
    <h2>Last Name: {props.lastName}</h2>
    <h2> Age: {props.age}</h2>
    </>
  )
}

