import React, { useState } from 'react';
import { Calendar, dateFnsLocalizer } from 'react-big-calendar';
import { format, parse, startOfWeek, getDay } from 'date-fns';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import enUS from 'date-fns/locale/en-US';
import './MapCalendar.css';

const locales = { 'en-US': enUS };

const localizer = dateFnsLocalizer({
    format,
    parse,
    startOfWeek,
    getDay,
    locales,
});

const MapCalendar = ({ user, users, setUsers }) => {
    const [events, setEvents] = useState([]);
    const [newEvent, setNewEvent] = useState({ title: '', start: '', end: '' });

    const handleAddEvent = () => {
        setEvents([...events, newEvent]);
        setNewEvent({ title: '', start: '', end: '' });
    };

    const handleDeleteEvent = (eventToDelete) => {
        setEvents(events.filter(event => event !== eventToDelete));
    };

    const handleRequestAppointment = (inviteCode) => {
        const invitedUser = users.find(user => user.inviteCode === inviteCode.trim());
        if (invitedUser) {
            alert(`Request sent to ${invitedUser.firstname} ${invitedUser.lastname}`);
        } else {
            alert('Invalid invite code.');
        }
    };

    const handleDateChange = (e) => {
        const dateStr = e.target.value;
        const parsedDate = parse(dateStr, 'dd/MM/yyyy', new Date());
        setNewEvent({ ...newEvent, start: parsedDate, end: parsedDate });
    };

    const handleStartTimeChange = (e) => {
        const startTime = e.target.value;
        const [hours, minutes] = startTime.split(':').map(part => parseInt(part, 10));
        const updatedStart = new Date(newEvent.start);
        updatedStart.setHours(hours);
        updatedStart.setMinutes(minutes);
        setNewEvent(prevEvent => ({
            ...prevEvent,
            start: updatedStart,
        }));
    };

    const handleEndTimeChange = (e) => {
        const endTime = e.target.value;
        const [hours, minutes] = endTime.split(':').map(part => parseInt(part, 10));
        const updatedEnd = new Date(newEvent.start);
        updatedEnd.setHours(hours);
        updatedEnd.setMinutes(minutes);
        setNewEvent(prevEvent => ({
            ...prevEvent,
            end: updatedEnd,
        }));
    };

    return (
        <div className="map-calendar">
            <div className="calendar-section">
                <h2>Calendar</h2>
                <div className="calendar-controls">
                    <input
                        type="text"
                        placeholder="Date (dd/mm/yyyy)"
                        value={format(newEvent.start, 'dd/MM/yyyy')}
                        onChange={handleDateChange}
                        className="datetime-input"
                    />
                    <input
                        type="text"
                        placeholder="Start Time (hh:mm)"
                        value={format(newEvent.start, 'HH:mm')}
                        onChange={handleStartTimeChange}
                        className="datetime-input"
                    />
                    <input
                        type="text"
                        placeholder="End Time (hh:mm)"
                        value={format(newEvent.end, 'HH:mm')}
                        onChange={handleEndTimeChange}
                        className="datetime-input"
                    />
                    <input
                        type="text"
                        placeholder="Title"
                        value={newEvent.title}
                        onChange={(e) => setNewEvent({ ...newEvent, title: e.target.value })}
                        className="event-title-input"
                    />
                    <button onClick={handleAddEvent} className="add-event-button">Add Event</button>
                </div>
                <Calendar
                    localizer={localizer}
                    events={events}
                    startAccessor="start"
                    endAccessor="end"
                    style={{ height: 500 }}
                    className="custom-calendar"
                    views={['month', 'agenda']}
                    components={{
                        toolbar: CustomToolbar,
                    }}
                />
                <div className="event-list">
                    {events.map((event, index) => (
                        <div key={index} className="event-item">
                            <span>{event.title}</span>
                            <button onClick={() => handleDeleteEvent(event)}>Delete</button>
                        </div>
                    ))}
                </div>
            </div>
            <div className="map-section">
                <h2>Map</h2>
                {/* Add your map component or integration here */}
            </div>
        </div>
    );
};

const CustomToolbar = ({ label }) => {
    return (
        <div className="rbc-toolbar">
            <span className="rbc-toolbar-label">{label}</span>
            <div className="rbc-btn-group">
                <button type="button" onClick={() => alert('Go to Today')}>
                    Today
                </button>
                <button type="button" onClick={() => alert('Go to Previous')}>
                    Back
                </button>
                <button type="button" onClick={() => alert('Go to Next')}>
                    Next
                </button>
            </div>
        </div>
    );
};

export default MapCalendar;
