import React, { useState } from 'react';
import './Dashboard.css';

// Menu component for displaying navigation buttons
const Menu = ({ onSelect }) => {
    return (
        <div className="menu">
            <button onClick={() => onSelect('profile')}>Profile</button>
            <button onClick={() => onSelect('communityFeed')}>Community Feed</button>
            <button onClick={() => onSelect('mapCalendar')}>Map/Calendar</button>
            <button onClick={() => onSelect('safety')}>Safety</button>
            <button onClick={() => onSelect('notebook')}>Notebook</button>
        </div>
    );
};

// Form component for submitting invite codes
const InviteCodeForm = ({ handleInviteCodeSubmit }) => {
    const [inviteCode, setInviteCode] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        handleInviteCodeSubmit(inviteCode);
        setInviteCode('');  // Clear the input field after submission
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                placeholder="Enter invite code"
                value={inviteCode}
                onChange={(e) => setInviteCode(e.target.value)}
            />
            <button type="submit">Submit</button>
        </form>
    );
};


const AcceptInviteForm = ({ user, handleAcceptInvitation }) => {
    const acceptInvitation = (inviter) => {
        handleAcceptInvitation(inviter, user);
    };

    return (
        <div>
            <h3>Pending Invitations</h3>
            <ul>
                {user.pendingInvitations.map((inviter, index) => (
                    <li key={index}>
                        {inviter}
                        <button onClick={() => acceptInvitation(inviter)}>Accept</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};


//placeholder for newsfeed content 
const NewsFeed = () => {
    return <div>News Feed Content</div>;
};

// Community Feed content
const CommunityFeed = ({ handleInviteCodeSubmit, handleAcceptInvitation }) => {
    return (
        <div>
            <div>Community Feed Content</div>
            <NewsFeed />
            <InviteCodeForm user={user} handleInviteCodeSubmit={handleInviteCodeSubmit} />
            <AcceptInviteForm user={user} handleAcceptInvitation={handleAcceptInvitation} />
            {/* Add other community feed features here */}
        </div>
    );

};

//  Profile content
const Profile = ({ user, users }) => {
    return (
        <div className="profile">
            <h2>Profile</h2>
            <p>First Name: {user.firstname}</p>
            <p>Last Name: {user.lastname}</p>
            <p>Date of Birth: {user.dob}</p>
            <p>Gender: {user.gender}</p>
            <p>Role: {user.role}</p>
            {user.role === 'Provider' && <p>License Number: {user.licenseNumber}</p>}
            {user.role === 'Pregnant' && <p>Weeks Pregnant: {user.weeksPregnant}</p>}
            <p>Invite Code: {user.inviteCode}</p>
        </div>
    );
};

const MapCalendar = () => {
    return <div>Map/Calendar Content</div>;
};

const Safety = () => {
    return <div>Safety Content</div>;
};

const Notebook = () => {
    return <div>Notebook Content</div>;
};

// Main layout component for displaying selected content area
const DashboardLayout = ({ user, users, setUsers }) => {
    const [selectedSection, setSelectedSection] = useState('communityFeed');

    const handleInviteCodeSubmit = ({ inviteCode, user, setUsers }) => {
        console.log('Received invite code:', inviteCode);
        const invitingUser = users.find(user => user.inviteCode === inviteCode.trim());
        console.log('Inviting user details:', invitingUser);

        if (invitingUser) {
            // Add current user's username to inviter's pendingInvitations array
            invitingUser.pendingInvitations.push(user.username);
            setUsers([...users]); // Update users state to reflect changes

            alert(`You have sent an invitation to ${invitingUser.firstname}.`);
            // Additional logic to notify the inviting user
        } else {
            alert('Invalid invite code.');
        }
    };

    const handleAcceptInvitation = (inviter, user, setUsers, users) => {
        console.log(`Accepting invitation from ${inviter.firstname}`);

        // Add inviter to current user's connections
        user.connections.push(inviter.username);

        // Add current user to inviter's connections
        inviter.connections.push(user.username);

        // Remove current user from inviter's pendingInvitations
        inviter.pendingInvitations = inviter.pendingInvitations.filter(code => code !== user.username);

        setUsers([...users]); // Update users state to reflect changes

        alert(`You have accepted ${inviter.firstname}'s invitation.`);
    };

    // Function to render content based on selected section
    const renderContent = () => {
        switch (selectedSection) {
            case 'profile':
                return <Profile user={user} />;
            case 'communityFeed':
                return <CommunityFeed handleInviteCodeSubmit={handleInviteCodeSubmit}
                    handleAcceptInvitation={handleAcceptInvitation} />;
            case 'mapCalendar':
                return <MapCalendar />;
            case 'safety':
                return <Safety />;
            case 'notebook':
                return <Notebook />;
            default:
                return <CommunityFeed handleInviteCodeSubmit={handleInviteCodeSubmit}
                    handleAcceptInvitation={handleAcceptInvitation} />;
        }
    };

    return (
        <div className="dashboard-layout">
            <Menu onSelect={setSelectedSection} />
            <div className="content-area">
                {renderContent()}
            </div>
        </div>
    );
};


const Dashboards = ({ user, users, setUsers, handleInviteCodeSubmit, handleAcceptInvitation }) => {
    return <DashboardLayout
        user={user}
        handleInviteCodeSubmit={handleInviteCodeSubmit}
        handleAcceptInvitation={handleAcceptInvitation}
        users={users}
        setUsers={setUsers}
    />;
};

export { Dashboards };
